sp_widget.client_script:
api.controller=function(spUtil, spModal) {
  /* widget controller */
  var c = this;

	c.incidents = c.data.table;

	c.incidentForm = function(incident){
		spModal.open({
			widget: 'widget-form',
			size: 'lg',
			widgetInput: {
				table: 'incident',
				sys_id: incident.sys_id
			},
			buttons: []
		})
	}

};

sp_widget.script:
(function() {

	// array of requests requested by the current user
	data.table = [];

	var gr = new GlideRecord('incident');
	gr.addQuery('caller_id', gs.getUserID());
	gr.query();

	while(gr.next()){
		var obj = {};
		obj.number = gr.getValue('number');
		obj.sys_id = gr.getValue('sys_id');
		obj.opened_at = gr.getValue('opened_at');
		data.table.push(obj);
	}

})();
