sp_widget.client_script:
api.controller=function($rootScope, $scope, spUtil, $uibModal, spModal) {
  /* widget controller */
  var c = this;

	// for pagination
	var begIndex = 0;
	var lastIndex = 9;

	c.movies = c.data.movies;
	c.mPage = _.slice(c.movies, begIndex, lastIndex);
	var movieData = '';

	//-- listen for
	$rootScope.$on('filterGenre', function(e, arg){
		if(arg){
			c.movies = c.data.movies.filter(function(item){
				if(item.genre.includes(arg))
					return item;
			})
		}
		else c.movies = c.data.movies;
		begIndex = 0;
		lastIndex = 9;
		c.mPage = _.slice(c.movies, begIndex, lastIndex);
	});

	// --- open modal
	c.openModal = function(movie) {
		c.modalInstance = $uibModal.open({
			templateUrl: 'movieModal',
			scope: $scope,
			size: 'md',
			controllerAs: 'modalC',
			controller: function() {
				var modalC = this;
				modalC.movie = movie;
				modalC.modalInstance = c.modalInstance;
			}
		});
	}

	// --- close modal
	c.closeModal = function(){
		c.modalInstance.close();
	}

	// --- add movie to cart
	c.addToCart = function(movie){
		if(movieData == '') movieData = movie.title
		else movieData += ','+ movie.title;
		c.data.movie = movieData;
		c.data.action = 'ADD TO CART';
		c.server.update().then(function(){
			c.data.action = undefined;
			c.data.movie = '';
		})

		c.modalInstance.close();
	}

	//--- pagination
	c.next = function(){
		console.log(c.movies.length - 9 + ' ' + c.movies.length);
		if(lastIndex + 9 >= c.movies.length){
			begIndex = c.movies.length - 10;
			lastIndex = c.movies.length -1;
		}else{
			begIndex += 9;
			lastIndex += 9;
		}
		c.mPage = _.slice(c.movies, begIndex, lastIndex);
	}

	c.previous = function(){
		if(begIndex - 9 <= 0){
			begIndex  = 0
			lastIndex = 9;
		}else{
			begIndex -= 9;
			lastIndex -= 9;
		}
		c.mPage = _.slice(c.movies, begIndex, lastIndex);
	}

};

