sp_widget.script:
(function() {
	// -- Get movies from server
	var movies = [];

	var gr = new GlideRecord('x_809069_bbuster_movies');
	gr.query();

	while(gr.next()){
		var object = {};
		object.poster = gr.getValue('poster');
		object.genre  = gr.getValue('genre');
		movies.push(object);
	}

	data.movies = movies;
	// -----------

})();
