sys_variable_value.value:
(function execute(inputs, outputs) {

  	// format movie title for omdb api query
  	// replace all spaces with dashes
	var regexp = new RegExp(' ', 'g');
  	var movieTitle = inputs.title.replace(regexp, '-');

  	// check if the movie is in the cmdb already
  	var exists = false;

  	var record = new GlideRecord('x_809069_bbuster_u_cmdb_ci_movie');
  	record.addEncodedQuery('GOTOu_titleLIKE'+inputs.title);
  	record.query();

  	while(record.next()){
      exists = true;
    }

  	// call omdb api get movie info
  	if(!exists){
		try { 
        	var r = new sn_ws.RESTMessageV2('x_809069_bbuster.OMDB API', 'GET');
         	r.setStringParameterNoEscape('title', movieTitle);

         	var response = r.execute();
          	var body = JSON.parse(response.getBody());
         	outputs.movie = body;
			outputs.noerror = body["Response"];
      }
		catch(ex) {
          	outputs.noerror = "False";
        	//outputs.errorMsg = ex.message;
   		}
    }

  	outputs.exists = exists;

})(inputs, outputs);

