sys_variable_value.value:
(function execute(inputs, outputs) {
	var movies = [];
  	var outOfStock = [];

  	var records = inputs.movies.split(',');

	records.forEach(function(movie){
    	var db = new GlideRecord('x_809069_bbuster_u_cmdb_ci_movie');
		db.addQuery('sys_id', movie);
      	db.query();

      	while(db.next()){
        	if(db.quantity > 0){
              	db.quantity = db.quantity - 1;
              	db.update();
            	movies.push(db.sys_id);
            }else{
            	outOfStock.push(db.sys_id);
            }
        }
    });

  	outputs.movies = movies.join(',');
  	outputs.outofstock = outOfStock.join(',');
})(inputs, outputs);

