sys_variable_value.value:
(function execute(inputs, outputs) {
	var movies = [];

  	gs.info(inputs.movies);

	inputs.movies.forEach(function(movie){
      	gs.info(movie);
    	var db = new GlideRecord('x_809069_bbuster_u_cmdb_ci_movie');
		db.addQuery('sys_id', movie);
      	db.query();

      	while(db.next()){
          	gs.info(movie + ' movie ');
        	if(movie.quantity > 0){
              	movies.quantity = movies.quantity - 1;
              	movies.update();
            	movies.push(movie.title);
            }
        }
    });

  	outputs.movies = movies;
})(inputs, outputs);

